import HighlightImage from "./assets/highlight.png";
import ProfileImage from "./assets/aakash.jpeg";

import "./App.css";

function App() {
  return (
    <div
      style={{ maxWidth: 450, margin: "0% auto", padding: 16, fontSize: 14 }}
    >
      <div style={{ flexDirection: "row", marginBottom: 24, display: "flex" }}>
        <div>
          <img
            src={ProfileImage}
            alt="aakash profile pic"
            style={{ width: "100%" }}
          />
          <div style={{ width: "40%" }}>Aakash Adesara</div>
        </div>
        <div style={{ width: "20%", textAlign: "right" }}>Work</div>
        <div style={{ width: "20%", textAlign: "right" }}>Blog</div>
        <div style={{ width: "20%", textAlign: "right" }}>Art</div>
        <div style={{ width: "20%", textAlign: "right" }}>Books</div>
      </div>
      <img
        src={HighlightImage}
        alt="aakash cover pic"
        style={{ width: "100%" }}
      />
      <p>
        I'm a California based engineer building in healthcare, venture, and
        crypto. In the past, I've been to 80+ hackathons, built rockets, and
        created some digital art. I love road biking, lo-fi instrumentals, and
        detailed animations.
      </p>
      <p>
        Currently, I am a growth engineer at Athelas, a fast-growing venture
        backed startup pushing the boundaries of modern healthcare. Outside of
        work, I operate a startup newsletter called DailyDropout.FYI with 40k+
        community members.
      </p>
    </div>
  );
}

export default App;
